function startTicking(){
    interval = setInterval(()=>{
        influxMsg = null;               // default: nothing to influx this tick
        ms += 1000 * direction;

        // countdown reached zero  flip to up & send start once
        if(direction === -1 && ms <= 0){
            ms = 0;  direction = 1;
            if(!startSent){
                influxMsg = { event: "start" };
                startSent = true;  endSent = false;    // arm an end
                context.set('startSent', true);
                context.set('endSent',   false);
            }
        }
        context.set('ms', ms);
        context.set('direction', direction);
        emitDisplay();
        node.send([displayMsg, null, influxMsg]);   // live UI; buttons unchanged
    }, 1000);

    running = true;
    context.set('running',  true);
    context.set('interval', interval);
    updateButtons();
}

